K('bridgedResource[Get Member]').load({
  attributes: ['Member ID', 'First Name', 'Last Name'],
  values: {'ID' : 'Allen'},
  success: function(bridgedData) {
    K('field[Member ID]').value(bridgedData['Member ID']);
    K('field[Student First Name]').value(bridgedData['First Name']);
    K('field[Student Last Name]').value(bridgedData['Last Name']);
    K('field[Billing Customer Id]').value(bridgedData['Billing Customer Id']);

    K('section[Student Information]').show();
    var tomorrow = new Date((new Date()).getTime() + 24 * 60 * 60 * 1000);
    bundle.config.widgets.xdsoftDatepicker({
      element: K("form").find("#firstinstallmentdate")[0],
      timepicker: false,
      displayDateFormat: "DD/MM/YYYY",
      options: {
        startDate: tomorrow,
        disabledDates: ['12-02-2020'], 
        minDate: 0,
        formatDate:"d-m-Y",
        validateOnBlur:false
      },
      onSelectDate:function(ct,$i){
        K('field[The first instalment is due on]').value(ct.dateFormat('YYYY-MM-DD'));
      }                
    });          
  }
});

K('field[Member ID]').value("");
