value=K('field[I promise to pay equal FREQUENCY instalments of]').name();
value=value.replace("FREQUENCY", K('field[Payment Frequency]').value());
$(K('field[I promise to pay equal FREQUENCY instalments of]').wrapper()).find("label").html(value);

if (K('field[Payment Frequency]').value()==="Weekly"){
  K('field[Admin Fee]').value("$1.30");
  K('field[Setup Fee]').value("$5.50");
} else if (K('field[Payment Frequency]').value()==="Fortnightly"){
  K('field[Admin Fee]').value("$1.95");
  K('field[Setup Fee]').value("$5.50");
} else if (K('field[Payment Frequency]').value()==="Monthly"){
  K('field[Admin Fee]').value("$2.95");
  K('field[Setup Fee]').value("$5.50");
} else if (K('field[Payment Frequency]').value()==="Quarterly"){
  K('field[Admin Fee]').value("$3.95");
  K('field[Setup Fee]').value("$5.50");
}

